import React from 'react';
import { Card, Row, Col } from 'react-bootstrap';
import './ResearchProjects.css';

const ResearchProjects = () => {
  const projects = [
    {
      title: '2023 - 2025 Elektrik Piyasaları için Yapay Zeka Destekli Öğrenme, Tahmin ve Optimizasyon Algoritmaları ile Etmen Temelli Benzetim Modeli Kurulması ve Piyasa İncelemeleri',
      funding: 'TÜBİTAK Projesi',
      link: 'https://www.example.com/project1', // Proje linki
      year: '2023 - 2025',
      team: 'Selçuklu S. B. (Yürütücü), Öztürk C., Muğaloğlu E., Arslan S., Bilgili F.',
    },
    // Diğer projeler
  ];

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-5">Research Projects</h2>
      <Row>
        {projects.map((project, index) => (
          <Col key={index} md={4} className="mb-4">
            <Card className="project-card">
              <Card.Body>
                <Card.Title>{project.title}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">{project.year}</Card.Subtitle>
                <Card.Text>{project.funding}</Card.Text>
                <Card.Text>{project.team}</Card.Text>
              </Card.Body>
              {/* Projeye tıklandığında link açılacak */}
              <a href={project.link} target="_blank" rel="noopener noreferrer" className="stretched-link">
                <Card.Footer className="text-center">Proje Özeti</Card.Footer>
              </a>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default ResearchProjects;
