import React from "react";
import { Card } from "react-bootstrap";

const ResearchProject = ({ project }) => {
  return (
    <Card className="mb-4 shadow-sm border-0">
      <Card.Body>
        <p className="text-muted mb-1" style={{ fontWeight: "bold", fontSize: "1rem" }}>
          {project.yearRange}
        </p>
        <h5 className="mb-3" style={{ fontWeight: "bold" }}>
          {project.title} <span role="img" aria-label="emoji">üåç</span>
        </h5>
        <p className="text-muted mb-2">{project.fundingSource}</p>
        <p className="mb-3">
          <a href={project.summaryLink} target="_blank" rel="noopener noreferrer" className="text-primary" style={{ fontStyle: "italic" }}>
            {project.summaryText}
          </a>
        </p>
        <p className="mb-0">
          {project.team.map((member, index) => (
            <span key={index}>
              <a href={member.profileLink} className="text-primary" target="_blank" rel="noopener noreferrer">
                {member.name}
              </a>
              {index < project.team.length - 1 && ", "}
            </span>
          ))}
        </p>
      </Card.Body>
    </Card>
  );
};

export default ResearchProject;
