import React from "react";
import { Card, Button, Container, Row, Col } from "react-bootstrap";

const projects = [
  {
    title: "Elektrik Piyasaları için Yapay Zekâ Destekli Öğrenme, Tahmin ve Optimizasyon Algoritmaları ile Etmen Temelli Benzetim Modeli Kurulması ve Piyasa İncelemeleri",
    year: "2023 - 2025",
    category: "Sürdürülebilir Kalkınma",
    funding: "TÜBİTAK Projesi",
    team: "Selçuklu S. B. (Yürütücü), Öztürk C., Muğaloğlu E., Arslan S., Bilgili F.",
    summaryLink: "https://avesis.erciyes.edu.tr/sbselcuklu/projeozeti"
  }
];

const ResearchProjects = () => {
  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">Research Projects</h2>
      <Row>
        {projects.map((project, index) => (
          <Col md={6} key={index} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title className="mb-3">
                  {project.year} - {project.title}
                </Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  {project.category}
                </Card.Subtitle>
                <Card.Text>
                  <strong>Funding Source:</strong> {project.funding}
                </Card.Text>
                <Card.Text>
                  <strong>Team:</strong> {project.team}
                </Card.Text>
                <Button
                  variant="primary"
                  href={project.summaryLink}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Proje Özeti
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default ResearchProjects;
