import React from "react";
import { Card, Button, Container, Row, Col } from "react-bootstrap";

const projects = [
  {
    title: "Renewable Energy Integration",
    description: "Research on integrating renewable energy sources into smart grids.",
    year: "2023",
    link: "https://avesis.erciyes.edu.tr/sbselcuklu/projeler"
  },
  {
    title: "AI in Industrial Optimization",
    description: "Exploring AI techniques to improve industrial systems.",
    year: "2022",
    link: "https://avesis.erciyes.edu.tr/sbselcuklu/projeler"
  }
];

const ResearchProjects = () => {
  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">Research Projects</h2>
      <Row>
        {projects.map((project, index) => (
          <Col md={4} key={index} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{project.title}</Card.Title>
                <Card.Text>{project.description}</Card.Text>
                <Card.Subtitle className="mb-2 text-muted">Year: {project.year}</Card.Subtitle>
                <Button
                  variant="primary"
                  href={project.link}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Learn More
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default ResearchProjects;
