import React from 'react';
import { Card, Row, Col } from 'react-bootstrap';
import './ResearchProjects.css';

const ResearchProjects = () => {
  const projects = [
    {
      yearRange: "2023 - 2025",
      title: "Elektrik Piyasaları için Yapay Zekâ Destekli Öğrenme, Tahmin ve Optimizasyon Algoritmaları ile Etmen Temelli Benzetim Modeli Kurulması ve Piyasa İncelemeleri",
      description: "TÜBİTAK Projesi",
      fundingSource: "TÜBİTAK",
      team: "Selçuklu S. B. (Yürütücü), Öztürk C., Muğaloğlu E., Arslan S., Bilgili F.",
      summaryLink: "#", // Proje özetine gidecek link
    },
    // Diğer projeler
  ];

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-5">Research Projects</h2>
      <Row>
        {projects.map((project, index) => (
          <Col key={index} md={4} className="mb-4">
            <Card className="project-card">
              <Card.Body>
                {/* Başlık kısmı tıklanabilir link */}
                <Card.Title>
                  <a href={project.summaryLink} target="_blank" rel="noopener noreferrer">
                    {project.title}
                  </a>
                </Card.Title>
                <Card.Subtitle className="mb-2 text-muted">{project.yearRange}</Card.Subtitle>
                <Card.Text>{project.description}</Card.Text>
                <Card.Text>{project.team}</Card.Text>
              </Card.Body>
              {/* Proje özeti kısmı */}
              <Card.Footer className="text-center">Proje Özeti</Card.Footer>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default ResearchProjects;
